import React, { useState } from "react";
import Card from "../weather_card/card";

let localValue = "India";

const Field = () => {
  const [val, setVal] = useState("");

  const valueChange = () => {
    localValue = val;
  };
  return (
    <>
      <input
        type="text"
        value={val}
        onChange={(e) => {
          setVal(e.target.value);
        }}
      />
      <input type="Submit" onSubmit={valueChange} />
      <Card />
    </>
  );
};

export default Field;
