import React, { useEffect, useState } from "react";
import axios from "axios";

const api_key = "21916b1a020d4a33bb534916203108";

const Card = (props) => {
  let url = `http://api.weatherapi.com/v1/current.json?key=${api_key}&q=${props.name}`;
  const [img, setImage] = useState("");
  const [conditon, setcondtn] = useState("");
  const [name, setName] = useState("");
  const [country, setctry] = useState("");
  useEffect((props) => {
    const current_data = async (props) => {
      const weather = await axios.get(url);
      console.log(weather.data);
      if (props.name === weather.data.location.name) {
        setImage(weather.data.current.condition.icon);
        setcondtn(weather.data.current.condition.text);
        setName(
          weather.data.location.name + ", " + weather.data.location.region
        );
        setctry(weather.data.location.country);
      } else {
        console.log("error");
      }
    };
    current_data();
  });

  return (
    <>
      <h2>{name}</h2>
      <h3>{country}</h3>
      <h5>{conditon}</h5>
      <img src={img} alt="weather_image" />
    </>
  );
};

export default Card;
