import React, { useEffect } from "react";
import axios from "axios";
import { useState } from "react";

const Card = () => {
  const [img, setImage] = useState("");
  const [conditon, setcondtn] = useState("");
  const [name, setName] = useState("");
  useEffect(() => {
    const current_data = async () => {
      const weather = await axios.get(
        "http://api.weatherapi.com/v1/current.json?key=21916b1a020d4a33bb534916203108&q=Atrauli"
      );
      console.log(weather.data);
      setImage(weather.data.current.condition.icon);
      setcondtn(weather.data.current.condition.text);
      setName(weather.data.location.name + "," + weather.data.location.region);
    };
    current_data();
  }, []);

  return (
    <>
      <img src={img} alt="weather image" />
      <h5>{conditon}</h5>
    </>
  );
};

export default Card;
