import React, { useState } from "react";
import Card from "../weather_card/card";

const Field = () => {
  const [val, setVal] = useState("India");
  return (
    <>
      <input
        type="text"
        value={val}
        onChange={(e) => {
          setVal(e.target.value);
        }}
      />
      <input type="Submit" onClick={() => window.location.reload(false)} />
      <Card name={val} />
    </>
  );
};

export default Field;
